import  "./Styles/Road_404.css"
import Table from "./Table";
import {useRef} from "react";

function Road_404(){

    let td = useRef(null)
    let isSpase=useRef(false)
    function GetTdRef(TdRef){
        td = TdRef()
    }

    function onKeyDown(event){
        if(event.code === "Spase"){

            if (!isSpase.current){
                isSpase.current=true
                let shift = 45;
                const interval = setInterval(() => {
                    shift--;
                    td.current.style.top = shift+"px"
                    if (shift === -27){
                        shift++;
                        clearInterval(interval)
                        setTimeout(()=>{
                            const interval = setInterval(() => {
                                shift++;
                                td.current.style.top = shift+"px"
                                if (shift === 46){
                                    clearInterval(interval)
                                    isSpase.current=false
                                }
                            },10)
                        },500)
                    }
                }, 10);
            }
        }
    }

    return(
        <table onKeyDown={onKeyDown} tabIndex={0}>
            <Table GetTdRefAction={GetTdRef}></Table>
        </table>
    )
}
export default Road_404
