import  "./Styles/Road_404.css"
import Table from "./Table";
import {useRef} from "react";

function Road_404(){

    let td = useRef(null)
    let space=useRef(false)
    let shift =useRef(45);
    function GetTdRef(TdRef){
        td = TdRef()
    }

    function onKeyDown(event){
        if(event.code === "Space"){
            let interval
            if (!space.current){
                space.current=true
                interval = setInterval(() => {
                    shift.current--;
                    td.current.style.top = shift.current+"px"
                    if (shift.current === -27){
                        shift++;
                        clearInterval(interval)
                        setTimeout(()=>{
                            interval = setInterval(() => {
                                shift.current=shift.current+1;
                                td.current.style.top = shift.current+"px"
                                if (shift.current === 46){
                                    clearInterval(interval)
                                    space.current=false
                                }
                            },10)
                        },500)
                    }
                }, 10);
            }
            else {
                clearInterval(interval)
                space.current=false
                onKeyDown(event)
            }
        }
    }

    return(
        <table onKeyDown={onKeyDown} tabIndex={0}>
            <Table GetTdRefAction={GetTdRef}></Table>
        </table>
    )
}
export default Road_404
